{"title":"otel-service-investigation-flow","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",

  "title":"Services Investigation Flow",
  "autosize": "pad",
  "padding": 5,
  "signals": [
    { "name": "w", "value": 15},
    { "name": "h", "value": 15},
    { "name": "aspect", "value": true },
    { "name": "smooth", "value": true},
    { "name": "align", "value": "left"},
    { "name": "baseline", "value": "top" },
    {"name": "nodeWidth", "value": 190},
    {"name": "nodeHeight", "value": 45},
    {"name": "verticalNodeGap", "value": 10},
    {"name": "horizontalNodeGap", "value": 140},
    {
      "name": "startingDepth",
      "value": 1,
      "on": [{"events": {"type": "timer", "throttle": 0}, "update": "-1"}]
    },
    {
      "name": "node",
      "value": 0,
      "on": [
        {"events": {"type": "timer", "throttle": 10}, "update": "0"}
      ]
    },
    {
      "name": "nodeHighlight",
      "value": "[0]",
      "on": [
        {
          "events": {"type": "mouseover", "markname": "node"},
          "update": "pluck(treeAncestors('treeCalcs', datum.id), 'id')"
        },
        {"events": {"type": "mouseout"}, "update": "[0]"}
      ]
    },
    {
      "name": "isExpanded",
      "value": 0,
      "on": [
        {
          "events": {"type": "click", "markname": "node"},
          "update": "datum.children > 0 && indata('treeClickStorePerm', 'id', datum.childrenIds[0])?true:false"
        }
      ]
    },
    {"name": "xrange", "update": "[0, width]"},
    {"name": "yrange", "update": "[0, height]"},
    {
      "name": "down",
      "value": null,
      "on": [
        {"events": "touchend", "update": "null"},
        {"events": "mousedown, touchstart", "update": "xy()"}
      ]
    },
    {
      "name": "xcur",
      "value": null,
      "on": [
        {"events": "mousedown, touchstart, touchend", "update": "slice(xdom)"}
      ]
    },
    {
      "name": "ycur",
      "value": null,
      "on": [
        {"events": "mousedown, touchstart, touchend", "update": "slice(ydom)"}
      ]
    },
    {
      "name": "delta",
      "value": [0, 0],
      "on": [
        {
          "events": [
            {
              "source": "window",
              "type": "mousemove",
              "consume": true,
              "between": [
                {"type": "mousedown"},
                {"source": "window", "type": "mouseup"}
              ]
            },
            {
              "type": "touchmove",
              "consume": true,
              "filter": "event.touches.length === 1"
            }
          ],
          "update": "down ? [down[0]-x(), down[1]-y()] : [0,0]"
        }
      ]
    },
    {
      "name": "anchor",
      "value": [0, 0],
      "on": [
        {
          "events": "wheel",
          "update": "[invert('xscale', x()), invert('yscale', y())]"
        },
        {
          "events": {
            "type": "touchstart",
            "filter": "event.touches.length===2"
          },
          "update": "[(xdom[0] + xdom[1]) / 2, (ydom[0] + ydom[1]) / 2]"
        }
      ]
    },
    {"name": "xext", "update": "[0,width]"},
    {"name": "yext", "update": "[0,height]"},
    {
      "name": "zoom",
      "value": 1,
      "on": [
        {
          "events": "wheel!",
          "force": true,
          "update": "pow(1.001, event.deltaY * pow(16, event.deltaMode))"
        },
        {
          "events": {"signal": "dist2"},
          "force": true,
          "update": "dist1 / dist2"
        }
      ]
    },
    {
      "name": "dist1",
      "value": 0,
      "on": [
        {
          "events": {
            "type": "touchstart",
            "filter": "event.touches.length===2"
          },
          "update": "pinchDistance(event)"
        },
        {"events": {"signal": "dist2"}, "update": "dist2"}
      ]
    },
    {
      "name": "dist2",
      "value": 0,
      "on": [
        {
          "events": {
            "type": "touchmove",
            "consume": true,
            "filter": "event.touches.length===2"
          },
          "update": "pinchDistance(event)"
        }
      ]
    },
    {
      "name": "xdom",
      "update": "slice(xext)",
      "on": [
        {
          "events": {"signal": "delta"},
          "update": "[xcur[0] + span(xcur) * delta[0] / width, xcur[1] + span(xcur) * delta[0] / width]"
        },
        {
          "events": {"signal": "zoom"},
          "update": "[anchor[0] + (xdom[0] - anchor[0]) * zoom, anchor[0] + (xdom[1] - anchor[0]) * zoom]"
        },
        {"events": "dblclick", "update": "[0,width]"}
      ]
    },
    {
      "name": "ydom",
      "update": "slice(yext)",
      "on": [
        {
          "events": {"signal": "delta"},
          "update": "[ycur[0] + span(ycur) * delta[1] / height, ycur[1] + span(ycur) * delta[1] / height]"
        },
        {
          "events": {"signal": "zoom"},
          "update": "[anchor[1] + (ydom[0] - anchor[1]) * zoom, anchor[1] + (ydom[1] - anchor[1]) * zoom]"
        },
        {"events": "dblclick", "update": "[0,height]"}
      ]
    },
    {"name": "scaledImgW", "update": "(w/ span(xdom))*width"},
    {
      "name": "scaledImgH",
      "update": "abs(h/ span(ydom))*height"
    },

    {"name": "scaledNodeWidth", "update": "(nodeWidth/ span(xdom))*width"},
    {
      "name": "scaledNodeHeight",
      "update": "abs(nodeHeight/ span(ydom))*height"
    },
    {"name": "scaledFont13", "update": "(13/ span(xdom))*width"},
    {"name": "scaledFont12", "update": "(12/ span(xdom))*width"},
    {"name": "scaledFont11", "update": "(11/ span(xdom))*width"},
    {"name": "scaledKPIHeight", "update": "(5/ span(xdom))*width"},
    {"name": "scaledLimit", "update": "(20/ span(xdom))*width"}
  ],
  "data": [
    {
      "name": "source",
      "values": [
        { "level1": "OTEL", "details": "Install Integration",
          "info":{
            "name":"OTEL Integration",
            "description":"Go here to read the documentation of the OTEL services",
            "references":"https://opentelemetry.io/"
          },
          "url":"http://localhost:5601/app/integrations#/available/otel-services", "kpi": 75 },
        {
          "level1": "OTEL",
          "level2": "OTEL Ingestion",
          "details": "Inspect Signal Rates",
          "info":{
            "name":"OTEL Ingestion",
            "description":"Go here to review the (OTEL) signals ingestion rate",
            "references":"https://opensearch.org/docs/latest/data-prepper/"
          },
          "url":"http://localhost:5601/app/dashboards#/view/data-ingestion-rate-dashboard-1_0_0_ID",
          "kpi": 80
        },
        {
          "level1": "OTEL",
          "level2": "OTEL Integrations",
          "details": "Services Dashboard",
          "url":"http://localhost:5601/app/dashboards#/view/otel-services-dashboard-1_0_0_ID",
          "info":{
            "name":"OTEL Services",
            "description":"Go here to monitor the application's services",
            "references":"https://opentelemetry.io/docs/demo/services/"
          },
          "kpi": 50
        },
        {
          "level1": "OTEL",
          "level2": "OTEL Integrations",
          "level3" : "Single Services Dashboard",
          "details": "Deep Dive Service Details",
          "info":{
            "name":"Single Service Deep Dive",
            "description":"Go here to analyze and monitor a specific service",
            "references":"https://opentelemetry.io/docs/demo/services/"
          },
          "url":"http://localhost:5601/app/dashboards#/view/single-service-correlated-dashboard-1_0_0_ID",
          "kpi": 50
        },
        {
          "level1": "OTEL",
          "level2": "OTEL Integrations",
          "level3" : "Single Services Dashboard",
          "level4" : "Service System Details",
          "details": "Service System Metrics",
          "url":"http://localhost:5601/app/dashboards#/view/apm-system-metrics-dashboard-1_0_0_ID",
          "info":{
            "name":"APM System Metrics Monitor",
            "description":"Go here to analyze the services system's metrics ",
            "references":"https://opentelemetry.io/docs/concepts/signals/metrics/"
          },
          "kpi": 50
        },
        {
          "level1": "OTEL",
          "level2": "OTEL Integrations",
          "level3" : "Single Services Dashboard",
          "level4" : "Service Network Details",
          "info":{
            "name":"APM Network Metrics Monitor",
            "description":"Go here to analyze the services network's metrics ",
            "references":"https://opentelemetry.io/docs/concepts/signals/metrics/"
          },
          "details": "Service Network Metrics",
          "url":"http://localhost:5601/app/dashboards#/view/apm-network-metrics-dashboard-1_0_0_ID",
          "kpi": 50
        }]},
    {
      "name": "wideToTall",
      "source": "source",
      "transform": [
        {
          "type": "formula",
          "expr": "{key: datum.level1,parent: null, details:datum.details, kpi:datum.kpi, link:datum.url, info:datum.info}",
          "as": "l1"
        },
        {
          "type": "formula",
          "expr": "{key: datum.level1+ '|'+datum.level2,parent: datum.level1, details:datum.details, kpi:datum.kpi, link:datum.url, info:datum.info}",
          "as": "l2"
        },
        {
          "type": "formula",
          "expr": "{key:datum.level1 + '|'+datum.level2+ '|'+datum.level3,parent: datum.level1+ '|'+datum.level2, details:datum.details, kpi:datum.kpi, link:datum.url, info:datum.info}",
          "as": "l3"
        },
        {
          "type": "formula",
          "expr": "{key:datum.level1 + '|'+datum.level2+ '|'+datum.level3+ '|'+ datum.level4,parent: datum.level1 + '|'+datum.level2+ '|'+datum.level3, details:datum.details, kpi:datum.kpi, link:datum.url, info:datum.info}",
          "as": "l4"
        },
        {
          "type": "formula",
          "expr": "{key:datum.level1 + '|'+datum.level2+ '|'+datum.level3+ '|'+ datum.level4+ '|'+ datum.level5,parent: datum.level1 + '|'+datum.level2+ '|'+datum.level3+ '|'+ datum.level4, details:datum.details, link:datum.url, info:datum.info, kpi:datum.kpi}",
          "as": "l5"
        },
        {
          "type": "formula",
          "expr": "{key:datum.level1 + '|'+datum.level2+ '|'+datum.level3+ '|'+ datum.level4+ '|'+ datum.level5+ '|'+ datum.level6,parent: datum.level1 + '|'+datum.level2+ '|'+datum.level3+ '|'+ datum.level4+ '|'+ datum.level5, details:datum.details, link:datum.url, info:datum.info, kpi:datum.kpi}",
          "as": "l6"
        },
        {"type": "fold", "fields": ["l1", "l2", "l3", "l4", "l5","l6"]},
        {"type": "project", "fields": ["key", "value"]},
        {"type": "formula", "expr": "datum.value.key", "as": "id"},
        {
          "type": "formula",
          "expr": "reverse(split(datum.value.key,'|'))[0]",
          "as": "title"
        },
        {"type": "formula", "expr": "datum.value.parent", "as": "parent"},
        {
          "type": "filter",
          "expr": "datum.title != 'null' && datum.title != 'undefined'"
        },
        {"type": "aggregate", "groupby": ["id", "parent", "title", "value"]},
        {"type": "formula", "expr": "datum.value.details", "as": "details"},
        {"type": "formula", "expr": "datum.value.kpi", "as": "kpi"},
        {"type": "formula", "expr": "datum.value.link", "as": "link"},
        {"type": "formula", "expr": "datum.value.info", "as": "info"}
      ]
    },
    {
      "name": "treeCalcs",
      "source": "wideToTall",
      "transform": [
        {"type": "stratify", "key": "id", "parentKey": "parent"},
        {
          "type": "tree",
          "method": {"signal": "'tidy'"},
          "separation": {"signal": "false"},
          "as": ["y", "x", "depth", "children"]
        },
        {"as": "parent", "type": "formula", "expr": "datum.parent"}
      ]
    },
    {
      "name": "treeChildren",
      "source": "treeCalcs",
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["parent"],
          "fields": ["parent"],
          "ops": ["values"],
          "as": ["childrenObjects"]
        },
        {
          "type": "formula",
          "expr": "pluck(datum.childrenObjects,'id')",
          "as": "childrenIds"
        }
      ]
    },
    {
      "name": "treeAncestors",
      "source": "treeCalcs",
      "transform": [
        {
          "type": "formula",
          "as": "treeAncestors",
          "expr": "treeAncestors('treeCalcs', datum.id, 'root')"
        },
        {"type": "flatten", "fields": ["treeAncestors"]},
        {
          "type": "formula",
          "expr": "datum.treeAncestors.parent",
          "as": "allParents"
        }
      ]
    },
    {
      "name": "treeChildrenAll",
      "source": "treeAncestors",
      "transform": [
        {
          "type": "project",
          "fields": [
            "allParents",
            "id",
            "name",
            "parent",
            "x",
            "y",
            "depth",
            "children"
          ]
        },
        {
          "type": "aggregate",
          "fields": ["parent", "parent", "id"],
          "ops": ["values", "count", "min"],
          "groupby": ["allParents"],
          "as": ["allChildrenObjects", "allChildrenCount", "id"]
        },
        {
          "type": "formula",
          "expr": "pluck(datum.allChildrenObjects,'id')",
          "as": "allChildrenIds"
        }
      ]
    },
    {
      "name": "treeClickStoreTemp",
      "source": "treeAncestors",
      "transform": [
        {
          "type": "project",
          "fields": ["id", "name", "parent", "x", "y", "depth", "children"]
        },
        {
          "type": "aggregate",
          "fields": ["id"],
          "ops": ["min"],
          "groupby": ["id", "name", "parent", "x", "y", "depth", "children"]
        }
      ]
    },
    {
      "name": "treeClickStorePerm",
      "values": [],
      "on": [
        {"trigger": "startingDepth>=0", "insert": "data('treeClickStoreTemp')"},
        {
          "trigger": "node",
          "insert": "!isExpanded? data('treeClickStoreTemp'):false"
        },
        {
          "trigger": "node",
          "remove": "isExpanded?data('treeClickStoreTemp'):false"
        }
      ]
    },
    {
      "name": "treeLayout",
      "source": "wideToTall",
      "transform": [
        {
          "type": "filter",
          "expr": "indata('treeClickStorePerm', 'id', datum.id)"
        },
        {"type": "stratify", "key": "id", "parentKey": "parent"},
        {
          "type": "tree",
          "method": {"signal": "'tidy'"},
          "nodeSize": [
            {"signal": "nodeHeight+verticalNodeGap"},
            {"signal": "nodeWidth+horizontalNodeGap"}
          ],
          "separation": {"signal": "false"},
          "as": ["y", "x", "depth", "children"]
        },
        {"type": "formula", "expr": "datum.y+(height/2)", "as": "y"},
        {"type": "formula", "expr": "scale('xscale',datum.x)", "as": "xscaled"},
        {
          "type": "formula",
          "expr": "datum.info",
          "as": "info"
        },
        {
          "type": "formula",
          "expr": "datum.link",
          "as": "link"
        },
        {"as": "parent", "type": "formula", "expr": "datum.parent"}
      ]
    },
    {
      "name": "fullTreeLayout",
      "source": "treeLayout",
      "transform": [
        {
          "type": "lookup",
          "from": "treeChildren",
          "key": "parent",
          "fields": ["id"],
          "values": ["childrenObjects", "childrenIds"]
        },
        {
          "type": "lookup",
          "from": "treeChildrenAll",
          "key": "allParents",
          "fields": ["id"],
          "values": ["allChildrenIds", "allChildrenObjects"]
        },
        {
          "type": "lookup",
          "from": "treeCalcs",
          "key": "id",
          "fields": ["id"],
          "values": ["children"]
        },
        {
          "type": "formula",
          "expr": "reverse(pluck(treeAncestors('treeCalcs', datum.id), 'id'))[1]",
          "as": "treeParent"
        }
      ]
    },
    {
      "name": "visibleNodes",
      "source": "fullTreeLayout",
      "transform": [
        {
          "type": "filter",
          "expr": "indata('treeClickStorePerm', 'id', datum.id)"
        }
      ]
    },
    {
      "name": "maxWidthAndHeight",
      "source": "visibleNodes",
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["depth"],
          "fields": ["depth", "x", "y"],
          "ops": ["count", "max", "max"],
          "as": ["count", "x", "y"]
        },
        {
          "type": "aggregate",
          "fields": ["depth", "count", "x", "y"],
          "ops": ["max", "max", "max", "max"],
          "as": ["maxDepth", "maxNodes", "maxX", "maxY"]
        }
      ]
    },
    {
      "name": "links",
      "source": "treeLayout",
      "transform": [
        {"type": "treelinks"},
        {
          "type": "linkpath",
          "orient": "horizontal",
          "shape": "diagonal",
          "sourceY": {"expr": "scale('yscale', datum.source.y)"},
          "sourceX": {"expr": "scale('xscale', datum.source.x+nodeWidth)"},
          "targetY": {"expr": "scale('yscale', datum.target.y)"},
          "targetX": {"expr": "scale('xscale', datum.target.x)"}
        },
        {
          "type": "filter",
          "expr": " indata('treeClickStorePerm', 'id', datum.target.id)"
        }
      ]
    }
  ],
  "scales": [
    {
      "name": "xscale",
      "zero": false,
      "domain": {"signal": "xdom"},
      "range": {"signal": "xrange"}
    },
    {
      "name": "yscale",
      "zero": false,
      "domain": {"signal": "ydom"},
      "range": {"signal": "yrange"}
    },
    {
      "name": "kpiscale",
      "zero": false,
      "domain": [0, 100],
      "range": {"signal": "[0,scaledNodeWidth]"}
    },
    {
      "name": "colour",
      "type": "ordinal",
      "range": [
        "#6f6f6f",
        "#4472C4",
        "#3A8E50",
        "#ED7D31",
        "#a63939",
        "#6338a6",
        "#3843a6",
        "#38a695"
      ],
      "domain": {"data": "visibleNodes", "field": "treeParent"}
    }
  ],
  "marks": [
    {
      "type": "path",
      "interactive": false,
      "from": {"data": "links"},
      "encode": {
        "update": {
          "path": {"field": "path"},
          "strokeWidth": {
            "signal": "indexof(nodeHighlight, datum.target.id)> -1? 2.5:0.4"
          },
          "stroke": {
            "scale": "colour",
            "signal": "reverse(pluck(treeAncestors('treeCalcs', datum.target.id), 'id'))[1]"
          }
        }
      }
    },
    {
      "name": "node",
      "description": "The parent node",
      "type": "group",
      "clip": false,
      "from": {"data": "visibleNodes"},
      "encode": {
        "update": {
          "x": {"field": "x", "scale": "xscale"},
          "width": {"signal": "scaledNodeWidth"},
          "yc": {"field": "y", "scale": "yscale"},
          "height": {"signal": "scaledNodeHeight"},
          "fill": {
            "signal": "merge(hsl(scale('colour', datum.treeParent)), {l:0.94})"
          },
          "stroke": {
            "signal": "merge(hsl(scale('colour', datum.treeParent)), {l:0.79})"
          },
          "cornerRadius": {"value": 2},
          "cursor": {"signal": "datum.children>0?'pointer':''"},
          "tooltip": {"signal": ""}
        }
      },
      "marks": [
        {
          "name": "linkImage",
          "type": "image",
          "interactive": true,
          "encode": {
            "enter": {
              "url": {"value": "https://icons.veryicon.com/png/o/education-technology/product-icon-of-apstar-big-data/opensearch-blue.png"}
            },
            "update": {
              "opacity": {"value": 1},
              "x": {"signal": "(180/ span(xdom))*width"},
              "y": {"signal": "-10"},
              "width": {"signal": "scaledImgW"},
              "height": {"signal": "scaledImgH"},
              "aspect": {"signal": "aspect"},
              "smooth": {"signal": "smooth"},
              "align": {"signal": "align"},
              "baseline": {"signal": "baseline"},
              "tooltip": {"signal": "parent.info"}

            },
            "hover": {
              "opacity": {"value": 0.5}
            }
          }
        },
        {
          "name": "infoImage",
          "type": "image",
          "interactive": true,
          "encode": {
            "enter": {
              "url": {"value": "https://opensearch.org/assets/brand/SVG/Mark/opensearch_mark_default.svg"}
            },
            "update": {
              "href": {"signal":"parent.link"},
              "opacity": {"value": 1},
              "x": {"signal": "item.mark.group.x1"},
              "y": {"signal": "-10"},
              "width": {"signal": "scaledImgW"},
              "height": {"signal": "scaledImgH"},
              "aspect": {"signal": "aspect"},
              "smooth": {"signal": "smooth"},
              "align": {"signal": "align"},
              "baseline": {"signal": "baseline"}
            },
            "hover": {
              "opacity": {"value": 0.5}
            }
          }
        },

        {
          "name": "highlight",
          "description": "highlight (seems like a Vega bug as this doens't work on the group element)",
          "type": "rect",
          "interactive": false,
          "encode": {
            "update": {
              "x": {"signal": "item.mark.group.x1"},
              "y": {"signal": "0"},
              "fill": {
                "signal": "indexof(nodeHighlight, parent.id)> -1? merge(hsl(scale('colour', parent.treeParent)), {l:0.82}):0"
              },
              "stroke": {
                "signal": "indexof(nodeHighlight, parent.id)> -1? merge(hsl(scale('colour', parent.treeParent)), {l:0.79}):0"
              },
              "height": {"signal": "item.mark.group.height"},
              "width": {"signal": "item.mark.group.width"}
            }
          }
        },
        {
          "name": "KPI background",
          "description": "KPI background",
          "type": "rect",
          "interactive": false,
          "clip": true,
          "encode": {
            "update": {
              "x": {"signal": "item.mark.group.x1"},
              "y": {"signal": "item.mark.group.height-scaledKPIHeight"},
              "height": {"signal": "scaledKPIHeight"},
              "width": {"signal": "(item.mark.group.width)"},
              "fill": {"scale": "colour", "signal": "parent.treeParent"},
              "opacity": {"value": 0.2}
            }
          }
        },
        {
          "name": "KPI",
          "description": "KPI",
          "type": "rect",
          "interactive": false,
          "clip": true,
          "encode": {
            "update": {
              "x": {"signal": "item.mark.group.x1"},
              "y": {"signal": "item.mark.group.height-scaledKPIHeight"},
              "height": {"signal": "scaledKPIHeight"},
              "width": {"signal": "scale('kpiscale',parent.kpi)"},
              "fill": {"scale": "colour", "signal": "parent.treeParent"}
            }
          }
        },
        {
          "type": "text",
          "interactive": false,
          "name": "name",
          "encode": {
            "update": {
              "x": {"signal": "(10/ span(xdom))*width"},
              "y": {"signal": "(6/ span(xdom))*width"},
              "fontWeight": {"value": "600"},
              "baseline": {"value": "top"},
              "fill": {"scale": "colour", "signal": "parent.treeParent"},
              "text": {"signal": "parent.details"},
              "fontSize": {"signal": "scaledFont13"},
              "limit": {"signal": "scaledNodeWidth-scaledLimit"},
              "font": {"value": "Calibri"}
            }
          }
        },
        {
          "type": "text",
          "interactive": false,
          "name": "title",
          "encode": {
            "update": {
              "x": {"signal": "(10/ span(xdom))*width"},
              "y": {"signal": "(22/ span(xdom))*width"},
              "align": {"value": "left"},
              "baseline": {"value": "top"},
              "fill": {"signal": "'#4D4B44'"},
              "text": {"signal": "parent.title"},
              "fontSize": {"signal": "scaledFont11"},
              "limit": {"signal": "scaledNodeWidth-scaledLimit"},
              "font": {"value": "Calibri"}
            }
          }
        },
        {
          "type": "text",
          "interactive": false,
          "name": "node children",
          "encode": {
            "update": {
              "x": {"signal": "item.mark.group.width - (9/ span(xdom))*width"},
              "y": {"signal": "item.mark.group.height/2"},
              "align": {"value": "right"},
              "baseline": {"value": "middle"},
              "fill": {"scale": "colour", "signal": "parent.treeParent"},
              "text": {"signal": "parent.children>0?parent.children:''"},
              "fontSize": {"signal": "scaledFont12"},
              "font": {"value": "Calibri"}
            }
          }
        }
      ]
    }
  ]
}"}}

{"title":"system-services-metrics-tree-map","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "padding": 5,

  "signals": [
    {
      "name": "labels", "value": true
    },
    {
      "name": "layout", "value": "tidy"
    },
    {
      "name": "links", "value": "diagonal"

    },
    {
      "name": "separation", "value": false
    }
  ],

  "data": [
{
  "name": "raw-data",
  "values": [
    {"id": 1, "name": "system"},
    {"id": 2, "name": "disk", "parent": 1},
    {"id": 3, "name": "io", "parent": 2},
    {"id": 4, "name": "operations", "parent": 2},
    {"id": 5, "name": "time", "parent": 2},
    {"id": 8, "name": "cpu", "parent": 1},
    {"id": 9, "name": "time", "parent": 8},
    {"id": 10, "name": "utilization", "parent": 8},
    {"id": 11, "name": "network", "parent": 1},
    {"id": 12, "name": "dropped_packets", "parent": 11},
    {"id": 13, "name": "errors", "parent": 11},
    {"id": 14, "name": "io", "parent": 11},
    {"id": 15, "name": "packets", "parent": 11},
    {"id": 16, "name": "memory", "parent": 1},
    {"id": 17, "name": "usage", "parent": 16},
    {"id": 18, "name": "utilization", "parent": 16},
    {"id": 23, "name": "connections", "parent": 11},
    {"id": 24, "name": "swap", "parent": 1},
    {"id": 25, "name": "usage", "parent": 24},
    {"id": 26, "name": "utilization", "parent": 24},
    {"id": 30, "name": "thread_count", "parent": 1}
  ]
},

    {
      "name": "tree",
      "source":"raw-data",
      "transform": [
        {
          "type": "stratify",
          "key": "id",
          "parentKey": "parent"
        },
        {
          "type": "lookup",
          "from": "raw-data",
          "key": "id",
          "fields": ["parent"],
          "as": ["parentData"]
        },
        {
          "type": "tree",
          "method": {"signal": "layout"},
          "size": [{"signal": "height"}, {"signal": "width - 100"}],
          "separation": {"signal": "separation"},
          "as": ["y", "x", "depth", "children"]
        }
      ]
    },
    {
      "name": "links",
      "source": "tree",
      "transform": [
        { "type": "treelinks" },
        {
          "type": "linkpath",
          "orient": "horizontal",
          "shape": {"signal": "links"}
        },
        {
          "type": "formula",
          "as": "parentName",
          "expr": "datum.source.name"
        }
      ]
    }
  ],

  "scales": [
    {
      "name": "parentColor",
      "type": "ordinal",
      "domain": {"data": "tree", "field": "parent"},
      "range": {"scheme": "category20"}
    }
  ],

  "marks": [
    {
      "type": "path",
      "from": {"data": "links"},
      "encode": {
        "update": {
          "path": {"field": "path"},
          "stroke": {"value": "#ccc"}
        }
      }
    },
    {
      "type": "symbol",
      "from": {"data": "tree"},
      "encode": {
        "enter": {
          "size": {"value": 200},
          "stroke": {"value": "#fff"}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"field": "y"},
          "fill": {"scale": "parentColor", "field": "parent"},
          "size": {"signal": "datum.children ? 150 : 200"}, 
          "shape": {"signal": "datum.children ? 'circle' : 'square'"}, 
          "strokeWidth": {"value": 1.5}
        },
        "hover": {
          "size":{"value": 400},
          "tooltip": {
            "signal": "datum.parentData ? {'Name': datum.name, 'Parent Group': datum.parentData.name} : {'Name': datum.name}"

          }
        }
      }
    },
    {
      "type": "text",
      "from": {"data": "tree"},
      "encode": {
        "enter": {
          "fill": {"value": "black"},
          "text": {"field": "name"},
          "fontSize": {"value": 12},
          "baseline": {"value": "middle"}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"field": "y"},
          "dx": {"signal": "datum.children ? -7 : 7"},
          "align": {"signal": "datum.children ? 'right' : 'left'"},
          "opacity": {"signal": "labels ? 1 : 0"}
        }
      }
    }
  ]
}
"}}

{"title":"multi-line-selectable-date-metrics-request-rate","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "title": "Metrics Requests Rate",
  "data": [
    {
      "name": "rawdata",
      "url": {
        "index": "otel-metrics-*",
        "%context%": true,
        "%timefield%": "time",
        "body": {
          "size": 0,
          "aggs": {
            "metrics": {
              "terms": {
                "field": "name.keyword",
                "size": 10
              },
              "aggs": {
                "time_buckets": {
                  "date_histogram": {
                    "field": "time",
                    "interval": {"%autointerval%": true},
                    "extended_bounds": {
                      "min": {"%timefilter%": "min"},
                      "max": {"%timefilter%": "max"}
                    },
                    "min_doc_count":0
                  }
                }
              }
            }

          }
        }
      },
      "format": {"property": "aggregations.metrics.buckets"}
    },
    {
      "name": "flatdata",
      "source": "rawdata",
      "transform": [
        {
          "type": "flatten",
          "fields": ["time_buckets.buckets"],
          "as": ["val"]
        },
        {
          "type": "formula",
          "as": "count",
          "expr":"datum.val.doc_count"
        },
        {
          "type": "formula",
          "as": "time",
          "expr": "timeFormat(utcParse(datum.val.key_as_string,'%Y-%m-%dT%H:%M:%S.%LZ'), '%B %d, %Y %H:%M')"
        },
          {
            "type": "formula",
            "as": "filter-key",
            "expr": "datum.key + '.keyword'"
         }
        ]
    },
    {
      "name": "hasSelection",
      "values": [{}],
      "transform": [
        {"type": "filter", "expr": "selected[0] != selected[1]"}
      ]
    }
  ],
  "scales": [
    {
      "name": "groupScale",
      "type": "band",
      "padding": 0.1,
      "domain": {"data": "rawdata", "field": "key", "sort": true},
      "range": "height"
    },
    {
      "name": "xScale",
      "type": "time",
      "domain": {"data": "flatdata", "field": "val.key"},
      "range": "width",
      "padding": 5
    },
    {
      "name": "yScale",
      "type": "linear",
      "domain": {"data": "flatdata", "field": "val.doc_count"},
      "range": [{"signal": "bandwidth('groupScale')"}, 0]
    },
    {
      "name": "colorScale",
      "type": "ordinal",
      "domain": {"data": "rawdata", "field": "key", "sort": true},
      "range": "category"
    }
  ],
  "axes": [
    {"orient": "bottom", "scale": "xScale", "tickCount": 5}
  ],
  "signals": [
    {
      "name": "currentX",
      "value": -1,
      "on": [
        {"events": "view:mousemove", "update": "clamp(x(), 0, width)"},
        {"events": "view:mouseout", "update": "-1"}
      ]
    },
    {
      "name": "selected",
      "value": [0, 0],
      "on": [
        {"events": "@grapharea:mousedown", "update": "[x(), x()]"},
        {
          "events": "[@grapharea:mousedown, window:mouseup] > window:mousemove!",
          "update": "[selected[0], clamp(x(), 0, width)]"
        },
        {
          "events": {"signal": "delta"},
          "update": "clampRange([anchor[0] + delta, anchor[1] + delta], 0, width)"
        },
        {
          "events": "[@leftEdge:mousedown, window:mouseup] > window:mousemove!",
          "update": "[clamp(x(), 0, width), selected[1]]"
        },
        {
          "events": "[@rightEdge:mousedown, window:mouseup] > window:mousemove!",
          "update": "[selected[0], clamp(x(), 0, width)]"
        }
      ]
    },
    {
      "name": "anchor",
      "value": null,
      "on": [
        {"events": "@selectedRect:mousedown", "update": "selected"}
      ]
    },
    {
      "name": "xDown",
      "value": 0,
      "on": [
        {"events": "@selectedRect:mousedown", "update": "x()"}
      ]
    },
    {
      "name": "delta",
      "value": 0,
      "on": [
        {"events": "[@selectedRect:mousedown, window:mouseup] > window:mousemove!", "update": "x() - xDown"}
      ]
    },
    {
      "name": "applyTimeFilter",
      "on": [
        {
          "events": "@applyTimeFilterButton:click, @selectedRect:dblclick",
          "update": "opensearchDashboardsSetTimeFilter(invert('xScale',selected[0]), invert('xScale',selected[1]))"
        },
        {"events": "@grapharea:dblclick", "update": "opensearchDashboardsSetTimeFilter('now-15d', 'now')"}
      ]
    }
  ],
  "marks": [
    {
      "name": "grapharea",
      "type": "group",
      "from": {
        "facet": {"name": "facets", "data": "rawdata", "field": "time_buckets.buckets"}
      },
      "encode": {
        "update": {
          "y": {"scale": "groupScale", "field": "key"},
          "height": {"scale": "groupScale", "band": 1},
          "x": {"value": 0},
          "width": {"signal": "width"},
          "fillOpacity": {"value": 0},
          "fill": {"value": "#000"},
          "tooltip": {
            "signal": "{'metrics': datum['key']}"
          }
        }
      },
      "axes": [
        {
          "orient": "left",
          "scale": "yScale",
          "title": {"signal": "parent.key"},
          "tickCount": 4,
          "encode": {
            "title": {
              "name": "extFilter",
              "interactive": true,
              "update": {
                "cursor": {"value": "pointer"},
                "fontSize": {"value": 8},
                "fill": {"value": "steelblue"}
              },
              "hover": {
                "fill": {"value": "firebrick"}
              }
            }
          }
        }
      ],
      "signals": [
        {
          "name": "updateErrFilterObj",
          "on": [
            {
              "events": {"source": "scope", "markname": "extErrorFilter", "type": "click"},
              "update": "{'match': {'status.code': {'query': 2, 'type': 'phrase'}}}"
            }
          ]
        },
        {
          "name": "applyErrorFilterObj",
          "on": [
            {
              "events": {"signal": "updateErrFilterObj"},
              "update": "length(domain('groupScale')) > 1 ? opensearchDashboardsAddFilter(updateErrFilterObj) : opensearchDashboardsRemoveFilter(updateErrFilterObj)"
            }
          ]
        },
        {
          "name": "updateFilterObj",
          "on": [
            {
              "events": {"source": "scope", "markname": "extFilter", "type": "click"},
              "update": "{'match': {'name.keyword': {'query': parent.key , 'type': 'phrase'}}}"
            }
          ]
        },
        {
          "name": "applyFilterObj",
          "on": [
            {
              "events": {"signal": "updateFilterObj"},
              "update": "length(domain('groupScale')) > 1 ? opensearchDashboardsAddFilter(updateFilterObj) : opensearchDashboardsRemoveFilter(updateFilterObj)"
            }
          ]
        }
      ],
      "marks": [
        {
          "type": "line",
          "from": {"data": "facets"},
          "interactive": false,
          "encode": {
            "update": {
              "x": {"scale": "xScale", "field": "key"},
              "y": {"scale": "yScale", "field": "doc_count"},
              "fill": {"scale": "colorScale", "field": {"parent": "key"}},
              "fillOpacity": {"value": 0.2},
              "stroke": {"scale": "colorScale", "field": {"parent": "key"}}
            }
          }
        },
        {
          "type": "symbol",
          "style": ["point"],
          "from": {
            "data": "facets"
          },
          "encode": {
            "update": {
              "x": {
                "scale": "xScale",
                "field": "key"
              },
              "y": {
                "scale": "yScale",
                "field": "doc_count"
              },
              "size": {
                "value": 8
              },
              "stroke": {"scale": "colorScale", "field": {"parent": "key"}},
              "tooltip": {
                "signal": "{'Date': timeFormat(datum['key'], '%Y-%m-%d %H:%M'), 'Value': datum['doc_count']}"
              }
            }
          }
        },
        {
          "type": "group",
          "from": {"data": "hasSelection"},
          "marks": [
            {
              "name": "selectedRect",
              "type": "rect",
              "encode": {
                "update": {
                  "height": {"scale": "groupScale", "band": 1},
                  "fill": {"value": "#333"},
                  "fillOpacity": {"value": 0.2},
                  "cursor": {"value": "move"},
                  "x": {"signal": "selected[0]"},
                  "x2": {"signal": "selected[1]"}
                }
              }
            },
            {
              "name": "leftEdge",
              "type": "rect",
              "encode": {
                "update": {
                  "height": {"scale": "groupScale", "band": 1},
                  "width": {"value": 2},
                  "fill": {"value": "firebrick"},
                  "cursor": {"value": "ew-resize"},
                  "x": {"signal": "selected[0]"}
                }
              }
            },
            {
              "name": "rightEdge",
              "type": "rect",
              "encode": {
                "update": {
                  "height": {"scale": "groupScale", "band": 1},
                  "width": {"value": 2},
                  "fill": {"value": "firebrick"},
                  "cursor": {"value": "ew-resize"},
                  "x": {"signal": "selected[1]"}
                }
              }
            }
          ]
        }
      ]
    },
    {
      "type": "group",
      "name": "applyTimeFilterButton",
      "from": {"data": "hasSelection"},
      "encode": {
        "update": {
          "cursor": {"value": "pointer"},
          "cornerRadius": {"value": 6},
          "fill": {"value": "#f5f5f5"},
          "stroke": {"value": "#c1c1c1"},
          "strokeWidth": {"value": 2},
          "xc": {"signal": "width/2"},
          "y": {"value": 30},
          "width": {"value": 80},
          "height": {"value": 30},
          "opacity": {"value": 1}
        },
        "hover": {
          "opacity": {"value": 0.7}
        }
      },
      "marks": [
        {
          "type": "text",
          "interactive": false,
          "encode": {
            "update": {
              "xc": {"field": {"group": "width"}, "mult": 0.5},
              "yc": {"field": {"group": "height"}, "mult": 0.5, "offset": 2},
              "align": {"value": "center"},
              "baseline": {"value": "middle"},
              "fontWeight": {"value": "bold"},
              "text": {"value": "Apply Filter"}
            }
          }
        }
      ]
    },
    {
      "type": "rule",
      "interactive": false,
      "encode": {
        "update": {
          "y": {"value": 0},
          "y2": {"signal": "height"},
          "stroke": {"value": "gray"},
          "strokeDash": {"value": [2, 1]},
          "x": {"signal": "max(currentX,0)"},
          "strokeOpacity": {"signal": "currentX > 0 ? 1 : 0"}
        }
      }
    }
  ]
}
"}}

{"title":"network-dropped-packets-type-metrics","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "background": "white",
  "padding": 5,
  "title": {"text": "Network Dropped Packets Metrics", "anchor": "start"},
  "data": [
    {"name": "param_37_store"},
    {
      "name": "source_0",
      "url": {
        "index": "otel-metrics-*",
        "body": {
          "size":5000,
          "query": {
            "bool": {
              "must": [
                "%dashboard_context-must_clause%",
                {
                  "range": {
                    "time": {
                      "%timefilter%": true
                    }
                  }
                }
              ],
              "must_not": [
                "%dashboard_context-must_not_clause%"
              ],
              "filter": [
                "%dashboard_context-filter_clause%"
              ]
            }
          }
        }
      },
      "format": {
        "property": "hits",
        "type": "json"
      },
      "transform": [
        {"type": "flatten", "fields": ["hits"], "as": ["record"]},
        {
          "type": "project",
          "fields": ["record._source.time","record._source.serviceName","record._source.value","record._source.attributes"],
          "as": ["time","serviceName","value","attrib"]
        },

      ]
    },
    {
      "name": "data_0",
      "source": "source_0",
      "transform": [
        {"type": "formula", "expr": "toDate(datum[\"time\"])", "as": "time"}
        {"type": "formula", "expr":"datum.attrib['metric.attributes.direction']", "as":"direction"}
        {"type": "formula", "expr":"datum.attrib['metric.attributes.device']", "as":"device"}
      ]
    },
    {
      "name": "data_2",
      "source": "data_0",
      "transform": [
        {
          "type": "filter",
          "expr": "!length(data(\"param_37_store\")) || vlSelectionTest(\"param_37_store\", datum)"
        },
        {
          "type": "stack",
          "groupby": ["serviceName"],
          "field": "value",
          "sort": {"field": [], "order": []},
          "as": ["value_start", "value_end"],
          "offset": "zero"
        },
        {
          "type": "filter",
          "expr": "isValid(datum[\"value\"]) && isFinite(+datum[\"value\"])"
        }
      ]
    }
  ],
  "signals": [
    {"name": "view_50_height", "value": 300},
    {"name": "concat_1_y_step", "value": 20},
    {
      "name": "concat_1_height",
      "update": "bandspace(domain('concat_1_y').length, 0.1, 0.05) * concat_1_y_step"
    },
    {
      "name": "unit",
      "value": {},
      "on": [
        {"events": "pointermove", "update": "isTuple(group()) ? group() : unit"}
      ]
    },
    {
      "name": "param_37",
      "update": "vlSelectionResolve(\"param_37_store\", \"union\")"
    }
  ],
  "layout": {"padding": 20, "columns": 1, "bounds": "full", "align": "each"},
  "marks": [
    {
      "type": "group",
      "name": "view_50_group",
      "style": "cell",
      "encode": {
        "update": {
          "width": {"signal": "width"},
          "height": {"signal": "view_50_height"}
        }
      },
      "signals": [
        {
          "name": "param_37_x",
          "value": [],
          "on": [
            {
              "events": {
                "source": "scope",
                "type": "pointerdown",
                "filter": [
                  "!event.item || event.item.mark.name !== \"param_37_brush\""
                ]
              },
              "update": "[x(unit), x(unit)]"
            },
            {
              "events": {
                "source": "window",
                "type": "pointermove",
                "consume": true,
                "between": [
                  {
                    "source": "scope",
                    "type": "pointerdown",
                    "filter": [
                      "!event.item || event.item.mark.name !== \"param_37_brush\""
                    ]
                  },
                  {"source": "window", "type": "pointerup"}
                ]
              },
              "update": "[param_37_x[0], clamp(x(unit), 0, width)]"
            },
            {
              "events": {"signal": "param_37_scale_trigger"},
              "update": "[scale(\"view_50_x\", param_37_time[0]), scale(\"view_50_x\", param_37_time[1])]"
            },
            {
              "events": [{"source": "view", "type": "dblclick"}],
              "update": "[0, 0]"
            },
            {
              "events": {"signal": "param_37_translate_delta"},
              "update": "clampRange(panLinear(param_37_translate_anchor.extent_x, param_37_translate_delta.x / span(param_37_translate_anchor.extent_x)), 0, width)"
            },
            {
              "events": {"signal": "param_37_zoom_delta"},
              "update": "clampRange(zoomLinear(param_37_x, param_37_zoom_anchor.x, param_37_zoom_delta), 0, width)"
            }
          ]
        },
        {
          "name": "param_37_time",
          "on": [
            {
              "events": {"signal": "param_37_x"},
              "update": "param_37_x[0] === param_37_x[1] ? null : invert(\"view_50_x\", param_37_x)"
            }
          ]
        },
        {
          "name": "param_37_scale_trigger",
          "value": {},
          "on": [
            {
              "events": [{"scale": "view_50_x"}],
              "update": "(!isArray(param_37_time) || (+invert(\"view_50_x\", param_37_x)[0] === +param_37_time[0] && +invert(\"view_50_x\", param_37_x)[1] === +param_37_time[1])) ? param_37_scale_trigger : {}"
            }
          ]
        },
        {
          "name": "param_37_tuple",
          "on": [
