        {"type": "formula", "expr":"datum.attrib['metric.attributes.device']", "as":"device"}
      ]
    },
    {
      "name": "data_2",
      "source": "data_0",
      "transform": [
        {
          "type": "filter",
          "expr": "!length(data(\"param_37_store\")) || vlSelectionTest(\"param_37_store\", datum)"
        },
        {
          "type": "stack",
          "groupby": ["serviceName"],
          "field": "value",
          "sort": {"field": [], "order": []},
          "as": ["value_start", "value_end"],
          "offset": "zero"
        },
        {
          "type": "filter",
          "expr": "isValid(datum[\"value\"]) && isFinite(+datum[\"value\"])"
        }
      ]
    }
  ],
  "signals": [
    {"name": "view_50_height", "value": 300},
    {"name": "concat_1_y_step", "value": 20},
    {
      "name": "concat_1_height",
      "update": "bandspace(domain('concat_1_y').length, 0.1, 0.05) * concat_1_y_step"
    },
    {
      "name": "unit",
      "value": {},
      "on": [
        {"events": "pointermove", "update": "isTuple(group()) ? group() : unit"}
      ]
    },
    {
      "name": "param_37",
      "update": "vlSelectionResolve(\"param_37_store\", \"union\")"
    }
  ],
  "layout": {"padding": 20, "columns": 1, "bounds": "full", "align": "each"},
  "marks": [
    {
      "type": "group",
      "name": "view_50_group",
      "style": "cell",
      "encode": {
        "update": {
          "width": {"signal": "width"},
          "height": {"signal": "view_50_height"}
        }
      },
      "signals": [
        {
          "name": "param_37_x",
          "value": [],
          "on": [
            {
              "events": {
                "source": "scope",
                "type": "pointerdown",
                "filter": [
                  "!event.item || event.item.mark.name !== \"param_37_brush\""
                ]
              },
              "update": "[x(unit), x(unit)]"
            },
            {
              "events": {
                "source": "window",
                "type": "pointermove",
                "consume": true,
                "between": [
                  {
                    "source": "scope",
                    "type": "pointerdown",
                    "filter": [
                      "!event.item || event.item.mark.name !== \"param_37_brush\""
                    ]
                  },
                  {"source": "window", "type": "pointerup"}
                ]
              },
              "update": "[param_37_x[0], clamp(x(unit), 0, width)]"
            },
            {
              "events": {"signal": "param_37_scale_trigger"},
              "update": "[scale(\"view_50_x\", param_37_time[0]), scale(\"view_50_x\", param_37_time[1])]"
            },
            {
              "events": [{"source": "view", "type": "dblclick"}],
              "update": "[0, 0]"
            },
            {
              "events": {"signal": "param_37_translate_delta"},
              "update": "clampRange(panLinear(param_37_translate_anchor.extent_x, param_37_translate_delta.x / span(param_37_translate_anchor.extent_x)), 0, width)"
            },
            {
              "events": {"signal": "param_37_zoom_delta"},
              "update": "clampRange(zoomLinear(param_37_x, param_37_zoom_anchor.x, param_37_zoom_delta), 0, width)"
            }
          ]
        },
        {
          "name": "param_37_time",
          "on": [
            {
              "events": {"signal": "param_37_x"},
              "update": "param_37_x[0] === param_37_x[1] ? null : invert(\"view_50_x\", param_37_x)"
            }
          ]
        },
        {
          "name": "param_37_scale_trigger",
          "value": {},
          "on": [
            {
              "events": [{"scale": "view_50_x"}],
              "update": "(!isArray(param_37_time) || (+invert(\"view_50_x\", param_37_x)[0] === +param_37_time[0] && +invert(\"view_50_x\", param_37_x)[1] === +param_37_time[1])) ? param_37_scale_trigger : {}"
            }
          ]
        },
        {
          "name": "param_37_tuple",
          "on": [
            {
              "events": [{"signal": "param_37_time"}],
              "update": "param_37_time ? {unit: \"view_50\", fields: param_37_tuple_fields, values: [param_37_time]} : null"
            }
          ]
        },
        {
          "name": "param_37_tuple_fields",
          "value": [{"field": "time", "channel": "x", "type": "R"}]
        },
        {
          "name": "param_37_translate_anchor",
          "value": {},
          "on": [
            {
              "events": [
                {
                  "source": "scope",
                  "type": "pointerdown",
                  "markname": "param_37_brush"
                }
              ],
              "update": "{x: x(unit), y: y(unit), extent_x: slice(param_37_x)}"
            }
          ]
        },
        {
          "name": "param_37_translate_delta",
          "value": {},
          "on": [
            {
              "events": [
                {
                  "source": "window",
                  "type": "pointermove",
                  "consume": true,
                  "between": [
                    {
                      "source": "scope",
                      "type": "pointerdown",
                      "markname": "param_37_brush"
                    },
                    {"source": "window", "type": "pointerup"}
                  ]
                }
              ],
              "update": "{x: param_37_translate_anchor.x - x(unit), y: param_37_translate_anchor.y - y(unit)}"
            }
          ]
        },
        {
          "name": "param_37_zoom_anchor",
          "on": [
            {
              "events": [
                {
                  "source": "scope",
                  "type": "wheel",
                  "consume": true,
                  "markname": "param_37_brush"
                }
              ],
              "update": "{x: x(unit), y: y(unit)}"
            }
          ]
        },
        {
          "name": "param_37_zoom_delta",
          "on": [
            {
              "events": [
                {
                  "source": "scope",
                  "type": "wheel",
                  "consume": true,
                  "markname": "param_37_brush"
                }
              ],
              "force": true,
              "update": "pow(1.001, event.deltaY * pow(16, event.deltaMode))"
            }
          ]
        },
        {
          "name": "param_37_modify",
          "on": [
            {
              "events": {"signal": "param_37_tuple"},
              "update": "modify(\"param_37_store\", param_37_tuple, true)"
            }
          ]
        }
      ],
      "marks": [
        {
          "name": "param_37_brush_bg",
          "type": "rect",
          "clip": true,
          "encode": {
            "enter": {
              "fill": {"value": "#333"},
              "fillOpacity": {"value": 0.125}
            },
            "update": {
              "x": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "signal": "param_37_x[0]"
                },
                {"value": 0}
              ],
              "y": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "value": 0
                },
                {"value": 0}
              ],
              "x2": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "signal": "param_37_x[1]"
                },
                {"value": 0}
              ],
              "y2": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "field": {"group": "height"}
                },
                {"value": 0}
              ]
            }
          }
        },
        {
          "name": "view_50_marks",
          "type": "symbol",
          "style": ["point"],
          "interactive": true,
          "from": {"data": "data_0"},
          "encode": {
            "update": {
              "opacity": {"value": 0.7},
              "fill": {"value": "transparent"},
              "stroke": [
                {
                  "test": "!length(data(\"param_37_store\")) || vlSelectionTest(\"param_37_store\", datum)",
                  "scale": "color",
