                },
                {"value": "lightgray"}
              ],
              "tooltip": {
                "signal": "{\"Service Name\": isValid(datum[\"serviceName\"]) ? datum[\"serviceName\"] : \"\"+datum[\"serviceName\"], \"Time\": timeFormat(datum[\"time\"], '%Y-%m-%d %H:%M:%S'), \"Value\": format(datum[\"value\"], \"\"), \"state\": isValid(datum[\"state\"]) ? datum[\"state\"] : \"\"+datum[\"state\"],  \"device\": isValid(datum[\"device\"]) ? datum[\"device\"] : \"\"+datum[\"device\"]}"
              },
              "ariaRoleDescription": {"value": "point"},
              "description": {
                "signal": "\"Time: \" + (timeFormat(datum[\"time\"], '%b %d, %Y')) + \"; Metric Value: \" + (format(datum[\"value\"], \"\")) + \"; serviceName: \" + (isValid(datum[\"serviceName\"]) ? datum[\"serviceName\"] : \"\"+datum[\"serviceName\"]) + \"; state: \" + (isValid(datum[\"state\"]) ? datum[\"state\"] : \"\"+datum[\"state\"]) + \"; Service Name: \" + (isValid(datum[\"serviceName\"]) ? datum[\"serviceName\"] : \"\"+datum[\"serviceName\"]) + \"; Value: \" + (format(datum[\"value\"], \"\")) + \"; State: \" + (isValid(datum[\"state\"]) ? datum[\"state\"] : \"\"+datum[\"state\"])"
              },
              "x": {"scale": "view_50_x", "field": "time"},
