          "clip": true,
          "encode": {
            "enter": {"fill": {"value": "transparent"}},
            "update": {
              "x": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "signal": "param_37_x[0]"
                },
                {"value": 0}
              ],
              "y": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "value": 0
                },
                {"value": 0}
              ],
              "x2": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "signal": "param_37_x[1]"
                },
                {"value": 0}
              ],
              "y2": [
                {
                  "test": "data(\"param_37_store\").length && data(\"param_37_store\")[0].unit === \"view_50\"",
                  "field": {"group": "height"}
                },
                {"value": 0}
              ],
              "stroke": [
                {"test": "param_37_x[0] !== param_37_x[1]", "value": "white"},
                {"value": null}
              ]
            }
          }
        }
      ],
      "axes": [
        {
          "scale": "view_50_x",
