{"title":"single-service-metrics-tree-map","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "padding": 5,

  "signals": [
    {
      "name": "labels", "value": true
    },
    {
      "name": "layout", "value": "tidy"    },
    {
      "name": "links", "value": "diagonal"
    },
    {
      "name": "separation", "value": false
    }
  ],

  "data": [
{
  "name": "raw-data",
  "values": [
     {"id": 0, "name": "recommendationservice"},
    {"id": 1, "name": "system", "parent": 0},
    {"id": 2, "name": "disk", "parent": 1},
    {"id": 3, "name": "io", "parent": 2},
    {"id": 4, "name": "operations", "parent": 2},
    {"id": 5, "name": "time", "parent": 2},
    {"id": 6, "name": "calls", "parent": 0},
    {"id": 7, "name": "duration", "parent": 0},
    {"id": 8, "name": "cpu", "parent": 1},
    {"id": 9, "name": "time", "parent": 8},
    {"id": 10, "name": "utilization", "parent": 8},
    {"id": 11, "name": "network", "parent": 1},
    {"id": 12, "name": "dropped_packets", "parent": 11},
    {"id": 13, "name": "errors", "parent": 11},
    {"id": 14, "name": "io", "parent": 11},
    {"id": 15, "name": "packets", "parent": 11},
    {"id": 16, "name": "memory", "parent": 1},
    {"id": 17, "name": "usage", "parent": 16},
    {"id": 18, "name": "utilization", "parent": 16},
    {"id": 19, "name": "process", "parent": 0},
    {"id": 20, "name": "runtime", "parent": 19},
    {"id": 21, "name": "cpython", "parent": 20},
    {"id": 22, "name": "gc_count", "parent": 21},
    {"id": 23, "name": "connections", "parent": 11},
    {"id": 24, "name": "swap", "parent": 1},
    {"id": 25, "name": "usage", "parent": 24},
    {"id": 26, "name": "utilization", "parent": 24},
    {"id": 27, "name": "cpu_time", "parent": 21},
    {"id": 28, "name": "memory", "parent": 21},
    {"id": 29, "name": "app_recommendations_counter", "parent": 0},
    {"id": 30, "name": "thread_count", "parent": 1}
  ]
},

    {
      "name": "tree",
      "source":"raw-data",
      "transform": [
        {
          "type": "stratify",
          "key": "id",
          "parentKey": "parent"
        },
        {
          "type": "lookup",
          "from": "raw-data",
          "key": "id",
          "fields": ["parent"],
          "as": ["parentData"]
        },
        {
          "type": "tree",
          "method": {"signal": "layout"},
          "size": [{"signal": "height"}, {"signal": "width - 100"}],
          "separation": {"signal": "separation"},
          "as": ["y", "x", "depth", "children"]
        }
      ]
    },
    {
      "name": "links",
      "source": "tree",
      "transform": [
        { "type": "treelinks" },
        {
          "type": "linkpath",
          "orient": "horizontal",
          "shape": {"signal": "links"}
        },
        {
          "type": "formula",
          "as": "parentName",
          "expr": "datum.source.name"
        }
      ]
    }
  ],

  "scales": [
    {
      "name": "parentColor",
      "type": "ordinal",
      "domain": {"data": "tree", "field": "parent"},
      "range": {"scheme": "category20"}
    }
  ],

  "marks": [
    {
      "type": "path",
      "from": {"data": "links"},
      "encode": {
        "update": {
          "path": {"field": "path"},
          "stroke": {"value": "#ccc"}
        }
      }
    },
    {
      "type": "symbol",
      "from": {"data": "tree"},
      "encode": {
        "enter": {
          "size": {"value": 200},
          "stroke": {"value": "#fff"}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"field": "y"},
          "fill": {"scale": "parentColor", "field": "parent"},
          "size": {"signal": "datum.children ? 150 : 200"}, 
          "shape": {"signal": "datum.children ? 'circle' : 'square'"}, 
          "strokeWidth": {"value": 1.5}
        },
        "hover": {
          "size":{"value": 400},
          "tooltip": {
            "signal": "datum.parentData ? {'Name': datum.name, 'Parent Group': datum.parentData.name} : {'Name': datum.name}"

          }
        }
      }
    },
    {
      "type": "text",
      "from": {"data": "tree"},
      "encode": {
        "enter": {
          "fill": {"value": "black"},
          "text": {"field": "name"},
          "fontSize": {"value": 12},
          "baseline": {"value": "middle"}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"field": "y"},
          "dx": {"signal": "datum.children ? -7 : 7"},
          "align": {"signal": "datum.children ? 'right' : 'left'"},
          "opacity": {"signal": "labels ? 1 : 0"}
        }
      }
    }
  ]
}
"}}

{"title":"traces-gantt-chart","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {
  "url" :{
      "%context%": "true",
      "%timefield%": "startTime",
      "index": "otel-v1-apm-span-*",
      "body": {
        "size": 75
      }
    }
    "format":{
      "type": "json",
      "property":"hits.hits"
    }
  },
  "transform": [
{"calculate": "datum._source.durationInNanos / 1000000", "as": "durationInMs"},
      {
    "window": [
      {
        "op": "max",
        "field": "durationInMs",
        "as": "maxDuration"
      }
    ],
    "frame": [null, null]
  },
  {
    "calculate": "datum.durationInMs / datum.maxDuration",
    "as": "normalizedDuration"
  },
  { "calculate": "datum._source.traceGroup", "as": "traceGroup"},
    {      "calculate": "toDate(datum._source.startTime)", "as": "currentDate"},
            {      "calculate": "toDate(datum._source.startTime)", "as": "startTime"},
  {
    "calculate": "format(datum.durationInMs, '.1f') + 'ms'",
    "as": "formattedDuration"
  },    {
      "calculate": "toDate(datum._source.endTime)",  "as": "endTime" },
    {
      "window": [{"op": "row_number", "as": "running_number"}],
      "groupby": ["_source.traceId"],
      "sort": [{"field": "currentDate"}]
    },
    {
      "calculate": "datum._source.traceId + ' | ' + datum._source.serviceName + ' | Start:' + toDate(datum._source.startTime) + ' #' + datum['running_number']",
      "as": "project_full_descr"
    }
  ],

  "title": {
    "text": "Order Gantt by: Trace > Span",
