{"title":"multi-line-selectable-date-services-request-rate","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "title": "Services Requests Rate",
  "data": [
    {
      "name": "rawdata",
      "url": {
        "index": "otel-v1-apm-span-*",
        "%context%": true,
        "%timefield%": "startTime",
        "body": {
          "aggs": {
            "services": {
              "terms": {
                "field": "serviceName",
                "size": 15
              },
              "aggs": {
                "time_buckets": {
                  "date_histogram": {
                    "field": "startTime",
                    "interval": {"%autointerval%": true},
                    "extended_bounds": {
                      "min": {"%timefilter%": "min"},
                      "max": {"%timefilter%": "max"}
                    },
                    "min_doc_count":0
                  },
                  "aggs": {
                    "duration": {
                      "avg": {
                        "missing": 0,
                        "script": {
                          "source": "!doc.containsKey('durationInNanos') || doc['durationInNanos'].empty ? 0 : doc['durationInNanos'].value / 1000000.0",
                          "lang": "painless"
                        }
                      }
                    }
                  }
                }
              }
            }
          },
          "size": 0
        }
      },
      "format": {"property": "aggregations.services.buckets"}
    },
    {
      "name": "flatdata",
      "source": "rawdata",
      "transform": [
        {
          "type": "flatten",
          "fields": ["time_buckets.buckets"],
          "as": ["val"]
        },
        {
          "type": "formula",
          "as": "count",
          "expr":"datum.val.doc_count"
        },
        {
          "type": "formula",
           "as": "duration",
           "expr": "datum.val.duration.value == null ? 0 : datum.val.duration.value"
         },
        {
          "type": "formula",
          "as": "time",
          "expr": "timeFormat(utcParse(datum.val.key_as_string,'%Y-%m-%dT%H:%M:%S.%LZ'), '%B %d, %Y %H:%M')"
        }
      ]
    },
    {
      "name": "hasSelection",
      "values": [{}],
      "transform": [
        {"type": "filter", "expr": "selected[0] != selected[1]"}
      ]
    }
  ],
  "scales": [
    {
      "name": "groupScale",
      "type": "band",
      "padding": 0.1,
      "domain": {"data": "rawdata", "field": "key", "sort": true},
      "range": "height"
    },
    {
      "name": "xScale",
      "type": "time",
      "domain": {"data": "flatdata", "field": "val.key"},
      "range": "width",
      "padding": 5
    },
    {
      "name": "durationScale",
      "type": "linear",
      "domain": {"data": "flatdata", "field": "duration"},
      "range": [{"signal": "bandwidth('groupScale')"}, 0]
    },
    {
      "name": "yScale",
      "type": "linear",
      "domain": {"data": "flatdata", "field": "val.doc_count"},
      "range": [{"signal": "bandwidth('groupScale')"}, 0]
    },
    {
      "name": "colorScale",
      "type": "ordinal",
      "domain": {"data": "rawdata", "field": "key", "sort": true},
      "range": "category"
    }
  ],
  "axes": [
    {"orient": "bottom", "scale": "xScale", "tickCount": 5}
  ],
  "signals": [
    {
      "name": "currentX",
      "value": -1,
      "on": [
        {"events": "view:mousemove", "update": "clamp(x(), 0, width)"},
        {"events": "view:mouseout", "update": "-1"}
      ]
    },
    {
      "name": "selected",
      "value": [0, 0],
      "on": [
        {"events": "@grapharea:mousedown", "update": "[x(), x()]"},
        {
          "events": "[@grapharea:mousedown, window:mouseup] > window:mousemove!",
          "update": "[selected[0], clamp(x(), 0, width)]"
        },
        {
          "events": {"signal": "delta"},
          "update": "clampRange([anchor[0] + delta, anchor[1] + delta], 0, width)"
        },
        {
          "events": "[@leftEdge:mousedown, window:mouseup] > window:mousemove!",
          "update": "[clamp(x(), 0, width), selected[1]]"
        },
        {
          "events": "[@rightEdge:mousedown, window:mouseup] > window:mousemove!",
          "update": "[selected[0], clamp(x(), 0, width)]"
        }
      ]
    },
    {
      "name": "anchor",
      "value": null,
      "on": [
        {"events": "@selectedRect:mousedown", "update": "selected"}
      ]
    },
    {
      "name": "xDown",
      "value": 0,
      "on": [
        {"events": "@selectedRect:mousedown", "update": "x()"}
      ]
    },
    {
      "name": "delta",
      "value": 0,
      "on": [
        {"events": "[@selectedRect:mousedown, window:mouseup] > window:mousemove!", "update": "x() - xDown"}
      ]
    },
    {
      "name": "applyTimeFilter",
      "on": [
        {
          "events": "@applyTimeFilterButton:click, @selectedRect:dblclick",
          "update": "opensearchDashboardsSetTimeFilter(invert('xScale',selected[0]), invert('xScale',selected[1]))"
        },
        {"events": "@grapharea:dblclick", "update": "opensearchDashboardsSetTimeFilter('now-15d', 'now')"}
      ]
    }
  ],
  "marks": [
    {
      "name": "grapharea",
      "type": "group",
      "from": {
        "facet": {"name": "facets", "data": "rawdata", "field": "time_buckets.buckets"}
      },
      "encode": {
        "update": {
          "y": {"scale": "groupScale", "field": "key"},
          "height": {"scale": "groupScale", "band": 1},
          "x": {"value": 0},
          "width": {"signal": "width"},
          "fillOpacity": {"value": 0},
          "fill": {"value": "#000"},
          "tooltip": {
            "signal": "{'Service': datum['key']}"
          }
        }
      },
      "axes": [
        {
          "orient": "left",
          "scale": "yScale",
          "title": {"signal": "parent.key"},
          "tickCount": 4,
          "encode": {
            "title": {
              "name": "extFilter",
              "interactive": true,
              "update": {
                "cursor": {"value": "pointer"},
                "fontSize": {"value": 8},
                "fill": {"value": "steelblue"}
              },
              "hover": {
                "fill": {"value": "firebrick"}
              }
            }
          }
        },
        {
          "orient": "right",
          "scale": "durationScale",
          "tickCount": 4,
          "title": {"signal": "parent.key"},
          "encode": {
            "title": {
              "name": "extErrorFilter",
              "interactive": true,
              "update": {
                "cursor": {"value": "pointer"},
                "fontSize": {"value": 8},
                "fill": {"value": "red"}
              },
              "hover": {
                "fill": {"value": "firebrick"}
              }
            }
          }
        }
      ],
      "signals": [
        {
          "name": "updateErrFilterObj",
          "on": [
            {
              "events": {"source": "scope", "markname": "extErrorFilter", "type": "click"},
              "update": "{'match': {'status.code': {'query': 2, 'type': 'phrase'}}}"
            }
          ]
        },
        {
          "name": "applyErrorFilterObj",
          "on": [
            {
              "events": {"signal": "updateErrFilterObj"},
              "update": "length(domain('groupScale')) > 1 ? opensearchDashboardsAddFilter(updateErrFilterObj) : opensearchDashboardsRemoveFilter(updateErrFilterObj)"
            }
          ]
        },
        {
          "name": "updateFilterObj",
          "on": [
            {
              "events": {"source": "scope", "markname": "extFilter", "type": "click"},
              "update": "{'match': {'serviceName': {'query': parent.key, 'type': 'phrase'}}}"
            }
          ]
        },
        {
          "name": "applyFilterObj",
          "on": [
            {
              "events": {"signal": "updateFilterObj"},
              "update": "length(domain('groupScale')) > 1 ? opensearchDashboardsAddFilter(updateFilterObj) : opensearchDashboardsRemoveFilter(updateFilterObj)"
            }
          ]
        }
      ],
      "marks": [
        {
          "type": "line",
          "from": {"data": "facets"},
          "interactive": false,
          "encode": {
            "update": {
              "x": {"scale": "xScale", "field": "key"},
              "y": {"scale": "yScale", "field": "doc_count"},
              "fill": {"scale": "colorScale", "field": {"parent": "key"}},
              "fillOpacity": {"value": 0.2},
              "stroke": {"scale": "colorScale", "field": {"parent": "key"}}
            }
          }
        },
        {
  "type": "rect",
  "from": {
    "data": "facets"
  },
  "encode": {
    "update": {
      "x": {
        "scale": "xScale",
        "field": "key"
      },
      "width": { "value": 15},
"cornerRadius": { "value": 5 }
      "y": {
        "scale": "durationScale",
        "field": "duration.value",
        "offset": -3
      },
      "y2": {"scale": "durationScale", "value": 0}

      "stroke": {"value":"black"},
      "fill":   {"scale": "colorScale", "field": {"parent": "key"}},
      "fillOpacity": {"value": 0.8},

      "tooltip": {
        "signal": "{'Date': timeFormat(datum['key'], '%Y-%m-%d %H:%M'), 'Value': datum['duration']}"
      }
    }
  }
},
        {
          "type": "symbol",
          "style": ["point"],
          "from": {
            "data": "facets"
          },
          "encode": {
            "update": {
              "x": {
                "scale": "xScale",
                "field": "key"
              },
              "y": {
                "scale": "yScale",
                "field": "doc_count"
              },
              "size": {
                "value": 8
              },
              "stroke": {"scale": "colorScale", "field": {"parent": "key"}},
              "tooltip": {
                "signal": "{'Date': timeFormat(datum['key'], '%Y-%m-%d %H:%M'), 'Value': datum['doc_count']}"
              }
            }
          }
        },
        {
          "type": "group",
          "from": {"data": "hasSelection"},
          "marks": [
            {
              "name": "selectedRect",
              "type": "rect",
              "encode": {
                "update": {
                  "height": {"scale": "groupScale", "band": 1},
                  "fill": {"value": "#333"},
                  "fillOpacity": {"value": 0.2},
                  "cursor": {"value": "move"},
                  "x": {"signal": "selected[0]"},
                  "x2": {"signal": "selected[1]"}
                }
              }
            },
            {
              "name": "leftEdge",
              "type": "rect",
              "encode": {
                "update": {
                  "height": {"scale": "groupScale", "band": 1},
                  "width": {"value": 2},
                  "fill": {"value": "firebrick"},
                  "cursor": {"value": "ew-resize"},
                  "x": {"signal": "selected[0]"}
                }
              }
            },
            {
              "name": "rightEdge",
              "type": "rect",
              "encode": {
                "update": {
                  "height": {"scale": "groupScale", "band": 1},
                  "width": {"value": 2},
                  "fill": {"value": "firebrick"},
                  "cursor": {"value": "ew-resize"},
                  "x": {"signal": "selected[1]"}
                }
              }
            }
          ]
        }
      ]
    },
    {
      "type": "group",
      "name": "applyTimeFilterButton",
      "from": {"data": "hasSelection"},
      "encode": {
        "update": {
          "cursor": {"value": "pointer"},
          "cornerRadius": {"value": 6},
          "fill": {"value": "#f5f5f5"},
          "stroke": {"value": "#c1c1c1"},
          "strokeWidth": {"value": 2},
          "xc": {"signal": "width/2"},
          "y": {"value": 30},
          "width": {"value": 80},
          "height": {"value": 30},
          "opacity": {"value": 1}
        },
        "hover": {
          "opacity": {"value": 0.7}
        }
      },
      "marks": [
        {
          "type": "text",
          "interactive": false,
          "encode": {
            "update": {
              "xc": {"field": {"group": "width"}, "mult": 0.5},
              "yc": {"field": {"group": "height"}, "mult": 0.5, "offset": 2},
              "align": {"value": "center"},
              "baseline": {"value": "middle"},
              "fontWeight": {"value": "bold"},
              "text": {"value": "Apply Filter"}
            }
          }
        }
      ]
    },
    {
      "type": "rule",
      "interactive": false,
      "encode": {
        "update": {
          "y": {"value": 0},
          "y2": {"signal": "height"},
          "stroke": {"value": "gray"},
          "strokeDash": {"value": [2, 1]},
          "x": {"signal": "max(currentX,0)"},
          "strokeOpacity": {"signal": "currentX > 0 ? 1 : 0"}
        }
      }
    }
  ]
}
"}}

{"title":"service-map-graph","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "padding": {"left": 0, "right": 0, "top": 0, "bottom": 0},
  "autosize": "pad",
  "background": "#f4f7ff",
  "signals": [
    {"name": "xrange", "update": "[0, width]"},
    {"name": "yrange", "update": "[height, 0]"},
    {"name": "xext", "update": "[0, width]"},
    {"name": "yext", "update": "[height, 0]"},
    {
      "name": "down",
      "value": null,
      "on": [
        {"events": "mouseup,touchend", "update": "null"},
        {"events": "mousedown, touchstart", "update": "xy()"},
        {"events": "symbol:mousedown, symbol:touchstart", "update": "null"}
      ]
    },
    {
      "name": "xcur",
      "value": null,
      "on": [{"events": "mousedown, touchstart, touchend", "update": "xdom"}]
    },
    {
      "name": "ycur",
      "value": null,
      "on": [{"events": "mousedown, touchstart, touchend", "update": "ydom"}]
    },
    {
      "name": "delta",
      "value": [0, 0],
      "on": [
        {
          "events": [
            {
              "source": "window",
              "type": "mousemove",
              "consume": true,
              "between": [
                {"type": "mousedown"},
                {"source": "window", "type": "mouseup"}
              ]
            },
            {
              "type": "touchmove",
              "consume": true,
              "filter": "event.touches.length === 1"
            }
          ],
          "update": "down ? [down[0]-x(), y()-down[1]] : [0,0]"
        }
      ]
    },
    {
      "name": "anchor",
      "value": [0, 0],
      "on": [
        {
          "events": "wheel",
          "update": "[invert('xscale', x()), invert('yscale', y())]"
        },
        {
          "events": {
            "type": "touchstart",
            "filter": "event.touches.length===2"
          },
          "update": "[(xdom[0] + xdom[1]) / 2, (ydom[0] + ydom[1]) / 2]"
        }
      ]
    },
    {
      "name": "zoom",
      "value": 1,
      "on": [
        {
          "events": "wheel!",
          "force": true,
          "update": "pow(1.001, event.deltaY * pow(16, event.deltaMode))"
        },
        {
          "events": {"signal": "dist2"},
          "force": true,
          "update": "dist1 / dist2"
        },
        {"events": [{"source": "view", "type": "dblclick"}], "update": "1"}
      ]
    },
    {
      "name": "dist1",
      "value": 0,
      "on": [
        {
          "events": {
            "type": "touchstart",
            "filter": "event.touches.length===2"
          },
          "update": "pinchDistance(event)"
        },
        {"events": {"signal": "dist2"}, "update": "dist2"}
      ]
    },
    {
      "name": "dist2",
      "value": 0,
      "on": [
        {
          "events": {
            "type": "touchmove",
            "consume": true,
            "filter": "event.touches.length===2"
          },
          "update": "pinchDistance(event)"
        }
      ]
    },
    {
      "name": "xdom",
      "update": "xext",
      "on": [
        {
          "events": {"signal": "delta"},
          "update": "[xcur[0] + span(xcur) * delta[0] / width, xcur[1] + span(xcur) * delta[0] / width]"
        },
        {
          "events": {"signal": "zoom"},
          "update": "[anchor[0] + (xdom[0] - anchor[0]) * zoom, anchor[0] + (xdom[1] - anchor[0]) * zoom]"
        },
        {"events": [{"source": "view", "type": "dblclick"}], "update": "xrange"}
      ]
    },
    {
      "name": "ydom",
      "update": "yext",
      "on": [
        {
          "events": {"signal": "delta"},
          "update": "[ycur[0] + span(ycur) * delta[1] / height, ycur[1] + span(ycur) * delta[1] / height]"
        },
        {
          "events": {"signal": "zoom"},
          "update": "[anchor[1] + (ydom[0] - anchor[1]) * zoom, anchor[1] + (ydom[1] - anchor[1]) * zoom]"
        },
        {"events": [{"source": "view", "type": "dblclick"}], "update": "yrange"}
      ]
    },
    {"name": "size", "update": "clamp(20 / span(xdom), 1, 1000)"},
    {
      "name": "cx",
      "update": "width / 2",
      "on": [
        {
          "events": "[symbol:mousedown, window:mouseup] > window:mousemove",
          "update": " cx==width/2?cx+0.001:width/2"
        }
      ]
    },
    {"name": "cy", "update": "height / 2"},
    {
      "name": "nodeRadiusKey",
      "description": "q=increase size, a=decrease size",
      "value": 8,
      "on": [
        {
          "events": "window:keypress",
          "update": "event.key=='a'&&nodeRadiusKey>1?nodeRadiusKey-1:event.key=='q'?nodeRadiusKey+1:nodeRadiusKey"
        }
      ]
    },
    {
      "name": "nodeRadius",
      "value": 8,
      "bind": {"input": "range", "min": 1, "max": 50, "step": 1},
      "on": [{"events": {"signal": "nodeRadiusKey"}, "update": "nodeRadiusKey"}]
    },
    {
      "name": "nodeCharge",
      "value": -30,
      "bind": {"input": "range", "min": -100, "max": 10, "step": 1}
    },
    {
      "name": "linkDistance",
      "value": 30,
      "bind": {"input": "range", "min": 5, "max": 300, "step": 1}
    },
    {
      "description": "State variable for active node fix status.",
      "name": "fix",
      "value": false,
      "on": [
        {
          "events": "symbol:mouseout[!event.buttons], window:mouseup",
          "update": "false"
        },
        {"events": "symbol:mouseover", "update": "fix || true", "force": true},
        {
          "events": "[symbol:mousedown, window:mouseup] > window:mousemove!",
          "update": "xy()",
          "force": true
        }
      ]
    },
    {
      "description": "Graph node most recently interacted with.",
      "name": "node",
      "value": null,
      "on": [
        {
          "events": "symbol:mouseover",
          "update": "fix === true ? datum.index : node"
        }
      ]
    },
    {
      "name": "nodeHover",
      "value": {"id": null, "connections": []},
      "on": [
        {
          "events": "symbol:mouseover",
          "update": "{'id':datum.index, 'connections':split(datum.sources+','+datum.targets,',')}"
        },
        {"events": "symbol:mouseout", "update": "{'id':null, 'connections':[]}"}
      ]
    },
    {
      "description": "Flag to restart Force simulation upon data changes.",
      "name": "restart",
      "value": false,
      "on": [{"events": {"signal": "fix"}, "update": "fix && fix.length"}]
    }
  ],
  "data": [
    {
      "name": "node-data-raw",
      "url": {
        "%context%": "true",
        "index": "otel-v1-apm-span-*",
        "body": {
          "size": 0,
          "aggs": {
            "services": {
              "terms": {
                "field": "serviceName",
                "size": 10000
              }
            }
          }
        }
      },
      "format": {
        "type": "json",
        "property": "aggregations.services.buckets"
      },
      "transform": [
       {
        "type": "formula",
